import React, { Component } from "react";
import Like from "./common/like";

class MoviesTable extends Component {
  raiseSort = (path) => {
    const sortColumn = { ...this.state.sortColumn };
    if (sortColumn.path === path)
      sortColumn.order = sortColumn.order === "asc" ? "desc" : "asc";
    else {
      sortColumn.path = path;
      sortColumn.order = "asc";
    }
  };

  render() {
    const { movies, onDelete, onLike, onSort } = this.props;

    return (
      <table className="table">
        <thead>
          <tr>
            <th onClick={() => onSort("title")} scope="col">
              Title
            </th>
            <th onClick={() => onSort("genre.name")} scope="col">
              Genre
            </th>
            <th onClick={() => onSort("numberInStock")} scope="col">
              Stock
            </th>
            <th onClick={() => onSort("dailyRentalRate")} scope="col">
              Rate
            </th>
            <th scope="col"></th>
            <th scope="col"></th>
          </tr>
        </thead>
        <tbody>
          {movies.map((movie) => (
            <tr key={movie._id}>
              <td scope="col">{movie.title}</td>
              <td scope="col">{movie.genre.name}</td>
              <td scope="col">{movie.numberInStock}</td>
              <td scope="col">{movie.dailyRentalRate}</td>
              <th scope="col">
                <Like liked={movie.liked} onClick={() => onLike(movie)} />
              </th>
              <td scope="col">
                <button onClick={() => onDelete(movie)} id={movie.id}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  }
}

export default MoviesTable;
