import React from "react";
import Joi from "joi-browser";
import Form from "./common/form";

class MovieForm extends Form {
  state = {
    data: { title: "", genre: "", stock: "", rate: "" },
    errors: {},
  };

  schema = {
    title: Joi.string().required().label("Title"),
    genre: Joi.string().required().label("Genre"),
    stock: Joi.number().integer().min(0).required().label("Stock"),
    rate: Joi.number().min(0).max(10).required().label("Rate"),
  };

  doSubmit = (history) => {
    //Call the server
    history.push("/movies");
    console.log("Submitted");
  };

  render() {
    const { match, history, data } = this.props;
    //{match.params.id}
    return (
      <div>
        <h1>Movie Form {this.state.data.title}</h1>
        <form onSubmit={this.handleSubmit}>
          {this.renderInput("title", "Title")}
          {this.renderInput("genreId", "Genre", this.state.genres)}
          {this.renderInput("stock", "Stock")}
          {this.renderInput("rate", "Rate")}
          {this.renderButton("Submit", "btn btn-primary")}
        </form>
      </div>
    );
  }
}

export default MovieForm;
